/**
 * The property name used to pass a remote element instance to the React component
 * that is configured to render it.
 */
const REMOTE_ELEMENT_PROP = Symbol.for('remote-dom.element');
const REMOTE_ELEMENT_ATTACHED_PROP = Symbol.for('remote-dom.element.attached');

export { REMOTE_ELEMENT_ATTACHED_PROP, REMOTE_ELEMENT_PROP };
